complete_test_script.powershell_script:
# PowerShell test script for Food Agent place details endpoint
Write-Host "Testing Food Agent Place Details Endpoint"

$BaseUrl = "http://localhost:8000"

# Test 1: Health check
Write-Host "`n1. Testing health check..."
Invoke-RestMethod -Uri "$BaseUrl/health" -Method Get

# Test 2: Get recommendations
Write-Host "`n2. Getting recommendations..."
$recommendationsBody = @{
    latitude = 19.0760
    longitude = 72.8777
    radius = 2000
    limit = 3
} | ConvertTo-Json

$recommendations = Invoke-RestMethod -Uri "$BaseUrl/recommendations" -Method Post -Body $recommendationsBody -ContentType "application/json"
$recommendations | ConvertTo-Json -Depth 10

# Test 3: Test with sample place ID
Write-Host "`n3. Testing place details..."
try {
    $placeDetails = Invoke-RestMethod -Uri "$BaseUrl/place/ChIJN1t_tDeuEmsRUsoyG83frY4" -Method Get
    $placeDetails | ConvertTo-Json -Depth 10
} catch {
    Write-Host "Error: $_" -ForegroundColor Red
}

# Test 4: Test with invalid place ID
Write-Host "`n4. Testing invalid place ID..."
try {
    Invoke-RestMethod -Uri "$BaseUrl/place/invalid_test_id" -Method Get
} catch {
    Write-Host "Expected error: $_" -ForegroundColor Yellow
}

Write-Host "`nTest completed!"
